				["init"] = {
					["custom"] = "--------------------------------------------------------------------------------------\n------------------------ Kink - Auto Healthstone Trader - Init -----------------------\n------------------------- Thanks for the help from Audiosmith ------------------------\n--------------------------------------------------------------------------------------\n\nlocal aura_env = aura_env\n\n-- Variables ---------\naura_env.stoneFound = false\naura_env.stoneloc = {nil, nil}\naura_env.stoneInTrade = false\n--------------------------------------------------------------------------------------\n\n-- Functions ---------\naura_env.locateStone = function()\n    aura_env.stoneFound = false\n    aura_env.stoneloc = {nil, nil}\n    local _,Major1 = GetItemInfo(19013)\n    local _,Major2 = GetItemInfo(19012)\n    local _,Major3 = GetItemInfo(9421)\n    local _,Greater1 = GetItemInfo(19011)\n    local _,Greater2 = GetItemInfo(19010)\n    local _,Greater3 = GetItemInfo(5510)\n    local _,Mid1 = GetItemInfo(19009)\n    local _,Mid2 = GetItemInfo(19008)\n    local _,Mid3 = GetItemInfo(5509)\n    local _,Lesser1 = GetItemInfo(19007)\n    local _,Lesser2 = GetItemInfo(19006)\n    local _,Lesser3 = GetItemInfo(5511)\n    local _,Minor1 = GetItemInfo(19005)\n    local _,Minor2 = GetItemInfo(19004)\n    local _,Minor3 = GetItemInfo(5512)    \n    for bag = 0, NUM_BAG_SLOTS do\n        for slot = 1, C_Container.GetContainerNumSlots(bag) do\n            if C_Container.GetContainerItemLink(bag, slot) == Major1 then \n                aura_env.stoneloc = {bag, slot} \n                aura_env.stoneFound = true\n            elseif C_Container.GetContainerItemLink(bag, slot) == Major2 then\n                aura_env.stoneloc = {bag, slot} \n                aura_env.stoneFound = true\n            elseif C_Container.GetContainerItemLink(bag, slot) == Major3 then \n                aura_env.stoneloc = {bag, slot} \n                aura_env.stoneFound = true\n            elseif C_Container.