						["unit"] = "nameplate",
						["subeventPrefix"] = "SPELL",
						["event"] = "Combat Log",
						["custom"] = "function(allstates, event, unitId)\n    if aura_env.nameplates == nil then\n        aura_env.nameplates = {}\n    end\n    if aura_env.lastUpdateTime == nil then\n        aura_env.lastUpdateTime = GetTime()\n    end\n    \n    local function getPartyMembers(target)\n        local members = {}\n        for i = 1, GetNumGroupMembers()-1, 1 do\n            local _, _, _, _, threatvalue = UnitDetailedThreatSituation (\"party\"..i, target)\n            if threatvalue then\n                table.insert(members, threatvalue)\n            end\n            local ppet = \"partypet\"..i\n            if (UnitExists(ppet)) then\n                local _, _, _, _, pett = UnitDetailedThreatSituation (ppet, target)\n                if pett then\n                    table.insert(members, pett)\n                end\n            end\n        end\n        return members\n    end\n    \n    local function getRaidMembers(target)\n        local members = {}\n        for i = 1, GetNumGroupMembers(), 1 do\n            local _, _, _, _, threatvalue = UnitDetailedThreatSituation (\"raid\"..i, target)\n            if threatvalue then\n                table.insert(members, threatvalue)\n            end\n            local ppet = \"raidpet\"..i\n            if (UnitExists(ppet)) then\n                local _, _, _, _, pett = UnitDetailedThreatSituation (ppet, target)\n                if pett then\n                    table.insert(members, pett)\n                end\n            end\n        end\n        return members\n    end\n    \n    local function getPetThreat(target)\n        if (UnitExists (\"pet\")) then\n            local _, _, _, _, threatvalue = UnitDetailedThreatSituation (\"pet\", target)\n            return threatvalue\n        end\n        return nil\n    end\n    \n    \n    local function truncate(number, decimals)\n        return number - (number % (0.1 ^ decimals))\n    end\n    \n    if event == \"NAME_PLATE_UNIT_REMOVED\" and unitId then\n        aura_env.nameplates[unitId] = nil\n        if allstates[unitId] then\n            allstates[unitId] = {\n                show = false,\n                changed = true\n            }\n        end\n    elseif event == \"NAME_PLATE_UNIT_ADDED\" and unitId then\n        aura_env.nameplates[unitId] = unitId;\n    else \n        if GetTime() > aura_env.lastUpdateTime + aura_env.config[\"updateInterval\"] then\n            aura_env.lastUpdateTime = GetTime()\n            for i, unit in pairs(aura_env.nameplates) do    \n                local isTanking, threatStatus, _, _, threatValue = UnitDetailedThreatSituation(\"player\", i)\n                \n                local displayValue = 0\n                local maxt\n                \n                local members = {}\n                if (IsInRaid()) then\n                    members = getRaidMembers(i)\n                elseif (IsInGroup()) then\n                    members = getPartyMembers(i)\n                end\n                local petT = getPetThreat(i)\n                if petT then\n                    table.insert(members, petT) \n                end\n                \n                if #members > 0 then\n                    table.sort(members)\n                    maxt = members[#members]\n                end\n                \n                if isTanking then\n                    displayValue = threatValue\n                    if maxt then\n                        displayValue = threatValue - maxt\n                    end\n                elseif threatStatus then\n                    displayValue = threatValue\n                    if maxt then\n                        displayValue = maxt - threatValue\n                    end\n                end\n                \n                displayValue = truncate(displayValue, 0)\n                \n                --print(\"display: \", displayValue)\n                \n                if displayValue > 0 then\n                    allstates[unit] = {\n                        changed = true,\n                        show = true,\n                        stacks = math.floor(displayValue / 100),\n                        status = threatStatus\n                    }\n                end\n            end\n        end\n    end\n    return true\nend",
						["customDuration"] = "function()\n    local ThreatLib = LibStub(\"ThreatClassic-1.0\")\n    \n    local isTanking, threatStatus, threatPercent, rawThreatPercent, threatValue = ThreatLib:UnitDetailedThreatSituation(\"player\", \"target\")\n    \n    aura_env.threat = aura_env.threat or {}\n    aura_env.threat.isTanking = isTanking\n    aura_env.threat.threatStatus = threatStatus\n    aura_env.threat.threatPercent = threatPercent\n    aura_env.threat.rawThreatPercent = rawThreatPercent\n    aura_env.threat.threatValue = threatValue\n    \n    return aura_env.threat.threatStatus\nend",
