						["custom"] = "function(s, event, _, subEvent, _, _, sourceName, _, _, _, _, _, _, _, spellName, _)\n    -- Maintain player list\n    if (event == \"GROUP_ROSTER_UPDATE\") or (event == \"PLAYER_ENTERING_WORLD\") or (event == \"PLAYER_TARGET_CHANGED\") then\n        \n        -- Remove\n        for _, state in pairs(s) do\n            local unitName = state.name;\n            local isUnitInvalid = (not UnitInParty(unitName)) and unitName ~= aura_env.playerName;\n            \n            if isUnitInvalid then\n                state.show = false;\n                state.changed = true;\n            end\n        end\n        \n        -- Add\n        for unit in WA_IterateGroupMembers(true) do\n            \n            local unitName = UnitName(unit);\n            local isUnitValid = (select(2,UnitClass(unitName)) == aura_env.class) and aura_env.warriorsToTrack[unitName];\n            \n            -- racist\n            --if aura_env.race ~= \"All\" then isUnitValid = isUnitValid and (UnitRace(unitName) == aura_env.race) end\n            \n            if isUnitValid then\n                s[unitName] = {\n                    show = true,\n                    changed = true, \n                    progressType = \"timed\",\n                    duration = aura_env.savedDuration[unitName] or 0,\n                    expirationTime = aura_env.savedExpiration[unitName] or 0,\n                    name = unitName,    \n                    index = aura_env.savedExpiration[unitName] or 0,\n                    rdyTimer = aura_env.savedExpiration[unitName] or 0,\n                }\n            end\n        end\n        \n        return true;\n    end\n    \n    -- TODO Handle offline/dead players\n    \n    -- Start timer\n    if (subEvent == \"SPELL_CAST_SUCCESS\") and UnitInParty(sourceName) then\n        if not aura_env.warriorsToTrack[sourceName] then return end\n        \n        local timestamp = GetTime();\n        \n        if (spellName == aura_env.spell) then \n            if s[sourceName]['rdyTimer'] < timestamp + aura_env.cd then\n                s[sourceName]['duration'] = aura_env.cd\n                s[sourceName]['expirationTime'] = timestamp + aura_env.cd\n                s[sourceName]['name'] = sourceName\n                s[sourceName]['index'] = timestamp + aura_env.cd \n                s[sourceName]['rdyTimer'] = timestamp + aura_env.cd\n                aura_env.savedDuration[sourceName] = s[sourceName].duration;\n                aura_env.savedExpiration[sourceName] = s[sourceName].expirationTime;\n                return true;\n            end\n        elseif aura_env.secondSpell[spellName] then\n            local secondCd = aura_env.secondCd\n            local secondCdTimer = timestamp + secondCd\n            \n            if s[sourceName]['rdyTimer'] < secondCdTimer then\n                s[sourceName]['duration'] = secondCd\n                s[sourceName]['expirationTime'] = secondCdTimer\n                s[sourceName]['name'] = sourceName\n                s[sourceName]['index'] = secondCdTimer\n                s[sourceName]['rdyTimer'] = secondCdTimer\n                aura_env.savedDuration[sourceName] = s[sourceName].duration;\n                aura_env.savedExpiration[sourceName] = s[sourceName].expirationTime;\n                return true\n            end\n        end\n    end\nend",
