				["init"] = {
					["custom"] = "-- Power Infusion Alerter\n-- Author:  Nayni-Golemagg\n-- Version: 1.0\n\n-- Spell ID and Name for Power Infusion\nlocal SPELL_ID_POWER_INFUSION = 10060\nlocal SPELL_NAME_POWER_INFUSION = GetSpellInfo(SPELL_ID_POWER_INFUSION)\n\n-- Patterns to search for in chat messages that indicate asking for PI.\nlocal PATTERNS_POWER_INFUSION_ASK = {\n    \"pi\",\n    \"power infusion\",\n    \"powerinfusion\"\n}\n\n-- Allow this WA to whisper to players about the ready state of PI.\nlocal ENABLE_WHISPERS = true\n\n--[[\n###########################################################\n      INIT\n\n      DO NOT TOUCH, UNLESS YOU HAVE EXPERIENCE WITH LUA!\n###########################################################\n]]\nlocal function IsPowerInfusionRequest(msg)\n    if not msg then return false end\n    \n    local msgLower = string.lower(msg)\n    \n    for _, pattern in ipairs(PATTERNS_POWER_INFUSION_ASK) do\n        local regexPattern = \".*\" .. pattern .. \".*\"\n        if string.match(msgLower, regexPattern) then\n            return true\n        end\n    end\n    \n    return false\nend\n\nlocal function GetRemainingSpellCooldown(spellId)\n    local startTime, duration = GetSpellCooldown(SPELL_ID_POWER_INFUSION)\n    local remaining = math.floor(startTime + duration - GetTime())\n    \n    return remaining\nend\n\nlocal function GetClassColor(playerName)\n    local classColor = \"FFffffff\"\n    \n    if playerName then\n        local name = string.gsub(playerName, \"%-[^|]+\", \"\")\n        local class, classFileName = UnitClass(name)\n        \n        if classFileName and RAID_CLASS_COLORS[classFileName] and RAID_CLASS_COLORS[classFileName][\"colorStr\"]  then\n            classColor = RAID_CLASS_COLORS[classFileName].colorStr\n        end\n    end\n    \n    return classColor\nend\n\nlocal function ColorText(txt, color)\n    if not color then return txt end\n    \n    if txt then\n        return \"|c\" .. color .. \"\" .. txt .. \"|r\"\n    end\nend\n\nlocal function FormatDuration(duration)\n    if not duration then return end\n    \n    local minutes = math.floor(duration / 60)\n    local seconds = duration % 60\n    \n    local durationString = \"\"\n    \n    if minutes > 1 then\n        durationString = durationString .. minutes .. \" minutes\"\n        if seconds > 0 then\n            durationString = durationString .. \" and \"\n        end\n    end\n    \n    if minutes == 1 then\n        durationString = durationString .. minutes .. \" minute\"\n        if seconds > 0 then\n            durationString = durationString .. \" and \"\n        end\n    end\n    \n    if seconds > 1 then\n        durationString = durationString .. seconds .. \" seconds\"\n    end\n    \n    if seconds == 1 then\n        durationString = durationString .. seconds .. \" second\"\n    end\n    \n    if minutes == 0 and seconds == 0 then\n        durationString = \"0 seconds\"\n    end\n    \n    return durationString\nend\n\nlocal function SendWhisper(msg, to)\n    if ENABLE_WHISPERS then\n        SendChatMessage(msg, \"WHISPER\", nil, to)\n    end\nend\n\nlocal function GetRequester()\n    return aura_env.currentRequester\nend\n\nlocal function GetRequesterName()\n    return aura_env.currentRequesterName\nend\n\nlocal function SetRequester(requester)\n    if not requester then return end\n    local name = string.gsub(requester, \"%-[^|]+\", \"\")\n    \n    aura_env.currentRequester = requester\n    aura_env.currentRequesterName = name\nend\n\nlocal function ClearRequester()\n    aura_env.currentRequester = nil\n    aura_env.currentRequesterName = nil\nend\n\nlocal function SetReceiver(receiver)\n    local name = string.gsub(receiver, \"%-[^|]+\", \"\")\n    aura_env.lastReceiver = name\nend\n\nlocal function IsLastReceiver(receiver)\n    local name = string.gsub(receiver, \"%-[^|]+\", \"\")\n    return aura_env.lastReceiver and aura_env.lastReceiver == name\nend\n\nlocal function EnableChatThrottle()\n    aura_env.chatIsThrottled = true\n    C_Timer.After(5, function()\n            WeakAuras.ScanEvents(\"WA_POWER_INFUSION_CLEAR_CHAT_THROTTLE\")\n    end)\nend\n\nlocal function DisableChatThrottle()\n    aura_env.chatIsThrottled = false\nend\n\nlocal function IsChatThrottled()\n    return aura_env.chatIsThrottled\nend\n\nlocal function Enable()\n    aura_env.enable = true\nend\n\nlocal function Disable()\n    aura_env.enable = false\nend\n\nlocal function IsEnabled()\n    return aura_env.enable\nend\n\naura_env.SPELL_ID_POWER_INFUSION = SPELL_ID_POWER_INFUSION\naura_env.SPELL_NAME_POWER_INFUSION = SPELL_NAME_POWER_INFUSION\naura_env.PATTERNS_POWER_INFUSION_ASK = PATTERNS_POWER_INFUSION_ASK\naura_env.SendWhisper = SendWhisper\naura_env.IsPowerInfusionRequest = IsPowerInfusionRequest\naura_env.GetRemainingSpellCooldown = GetRemainingSpellCooldown\naura_env.GetClassColor = GetClassColor\naura_env.ColorText = ColorText\naura_env.FormatDuration = FormatDuration\naura_env.GetRequester = GetRequester\naura_env.GetRequesterName = GetRequesterName\naura_env.SetRequester = SetRequester\naura_env.ClearRequester = ClearRequester\naura_env.SetReceiver = SetReceiver\naura_env.IsLastReceiver = IsLastReceiver\naura_env.EnableChatThrottle = EnableChatThrottle\naura_env.DisableChatThrottle = DisableChatThrottle\naura_env.IsChatThrottled = IsChatThrottled\naura_env.Enable = Enable\naura_env.Disable = Disable\naura_env.IsEnabled = IsEnabled\naura_env.currentRequester = nil\naura_env.currentRequesterName = nil\naura_env.lastReceiver = nil\naura_env.chatIsThrottled = false\naura_env.enable = false",
