					["custom"] = "aura_env.stalaggId = \"15929\"\naura_env.feugenId = \"15930\"\naura_env.load = true\naura_env.stalaggHP = 1\naura_env.feugenHP = 1\n\naura_env.scanTargets = function()\n    aura_env.stallagFound = false\n    aura_env.feugenFound = false\n    for unit in WA_IterateGroupMembers() do\n        local guid = UnitGUID(unit..\"target\")\n        if guid ~= nil then\n            local npcId = select(6, strsplit(\"-\", guid))\n            if npcId == aura_env.stalaggId then -- Stalagg\n                aura_env.stalaggHP = UnitHealth(unit..\"target\") / UnitHealthMax(unit..\"target\")\n                if aura_env.feugenFound then\n                    return true\n                else \n                    aura_env.stallagFound = true\n                end\n            elseif npcId == aura_env.feugenId then -- Feugen\n                aura_env.feugenHP = UnitHealth(unit..\"target\") / UnitHealthMax(unit..\"target\")\n                if aura_env.stallagFound then\n                    return true\n                else \n                    aura_env.feugenFound = true\n                end\n            end\n        end\n    end\n    if aura_env.stallagFound == false and aura_env.feugenFound == false then\n        aura_env.load = false\n        aura_env.stallagHP = 0\n        aura_env.feugenHP = 0\n    end\n    if aura_env.stallagFound == false then \n        aura_env.stallagHP = 0\n    end\n    if aura_env.feugenFound == false then\n        aura_env.feugenHP = 0\n    end\nend",
