						["custom"] = "function(s, event)\n    \n    if s[1] and aura_env.load and event == \"UNIT_HEALTH\" and (not aura_env.last1 or aura_env.last1 < GetTime() - aura_env.config.throttle) then\n        aura_env.last1 = GetTime()\n        aura_env.scanTargets()\n        if aura_env.stalaggHP == 0 then\n            s[1].changed = true\n            s[1].value = 0\n            s[1].percentRight = \"DEAD\"\n        else\n            s[1].changed = true\n            s[1].value = aura_env.stalaggHP\n            s[1].percentRight = math.floor(aura_env.stalaggHP * 100)..\"%\"\n        end\n        return true\n    elseif aura_env.load and event == \"UNIT_HEALTH\" and (not aura_env.last1 or aura_env.last1 < GetTime() - aura_env.config.throttle) then\n        aura_env.last1 = GetTime()\n        aura_env.scanTargets()\n        if aura_env.stalaggHP > 0 then\n            s[1] = {\n                show = true,\n                changed = true,\n                progressType = \"static\",\n                total = 1,\n                inverse = true,\n                value = aura_env.stalaggHP,\n                percentRight = math.floor(aura_env.stalaggHP * 100)..\"%\",\n                sort = 1,\n                nameRight = \"Stalagg\"\n            }\n            return true\n        end\n    end\n    \n    if s[2] and aura_env.load and event == \"UNIT_HEALTH\" and (not aura_env.last2 or aura_env.last2 < GetTime() - aura_env.config.throttle) then\n        aura_env.last2 = GetTime()\n        aura_env.scanTargets()\n        if aura_env.feugenHP == 0 then\n            s[2].changed = true\n            s[2].value = 0\n            s[2].percentLeft = \"DEAD\"\n        else\n            s[2].changed = true\n            s[2].value = aura_env.feugenHP\n            s[2].percentLeft = math.floor(aura_env.feugenHP * 100)..\"%\"\n        end\n        return true\n    elseif aura_env.load and event == \"UNIT_HEALTH\" and (not aura_env.last2 or aura_env.last2 < GetTime() - aura_env.config.throttle) then\n        aura_env.last2 = GetTime()\n        aura_env.scanTargets()\n        if aura_env.feugenHP > 0 then\n            s[2] = {\n                show = true,\n                changed = true,\n                progressType = \"static\",\n                total = 1,\n                value = aura_env.feugenHP,\n                percentLeft = math.floor(aura_env.feugenHP * 100)..\"%\",\n                sort = 2,\n                nameLeft = \"Feugen\"\n            }\n            return true\n        end\n    end\n    \n    if aura_env.load == false then\n        for _, state in pairs(s) do\n            state.show = false\n            state.changed = true\n        end\n        return true\n    elseif aura_env.load and event == \"PLAYER_TARGET_CHANGED\" then\n        local guid = UnitGUID(\"target\")\n        if guid ~= nil then\n            local npcId = select(6, strsplit(\"-\", guid))\n            if npcId == \"15928\" then -- Thaddius 15928\n                aura_env.load = false\n                for _, state in pairs(s) do\n                    state.show = false\n                    state.changed = true\n                end\n                return true\n            end\n        end\n    end\n    \n    if WeakAuras.IsOptionsOpen() then\n        s[1] = {\n            show = true,\n            changed = true,\n            progressType = \"static\",\n            total = 1,\n            inverse = true,\n            value = 1,\n            percentRight = \"100%\",\n            sort = 1,\n            nameRight = \"Stalagg\"\n        }\n        s[2] = {\n            show = true,\n            changed = true,\n            progressType = \"static\",\n            total = 1,\n            value = 1,\n            percentLeft = \"100%\",\n            sort = 2,\n            nameLeft = \"Feugen\"\n        }\n        return true\n    end\n    \nend",
