					["custom"] = "local _\nlocal _ae = aura_env\nlocal _cfg = _ae.config\nlocal _wase = WeakAuras.ScanEvents\n\n_ae.show = false\n_ae.text = \"\"\n_ae.lastUpdate = -1\n\n_ae.hooked = {}\nlocal hookCore = function()\n    WeakAuras.ScanEvents('WA_WF_NOW_HOOK', aura_env.id, aura_env)\nend\n\n_ae.classColorLookup = {\n    WARRIOR  = 'C79C6E', --  warrior 1\n    PALADIN  = 'F58CBA', --  paladin 2\n    HUNTER  = 'ABD473', --  hunter 3\n    ROGUE  = 'FFF569', --  rogue 4\n    PRIEST  = 'FFFFFF', --  priest 5\n    SHAMAN  = '0070DE', --  shaman 7\n    MAGE  = '69CCF0', --  mage 8\n    WARLOCK  = '9482C9', --  warlock 9\n    DRUID = 'FF7D0A', --  druid 11\n}\n\n_ae.meleFilter = {\n    WARRIOR = true,\n    PALADIN = true,\n    HUNTER = false,\n    ROGUE = true,\n    PRIEST = false,\n    SHAMAN = true,\n    MAGE = false,\n    WARLOCK = false,\n    DRUID= false,\n}\n\nlocal colorize = function(aPlayerObj) \n    local tColor = _ae.classColorLookup[aPlayerObj.class]\n    if aPlayerObj.eid == -1 then tColor = '505050' end\n    return  '|cFF' .. tColor .. aPlayerObj.name .. '|r'\nend\n\nlocal createIcon = function(aPlayerObj) \n    if aPlayerObj.eid == -1 then return '' end\n    return '|T' .. _ae._mainLib.tmpEnchant[aPlayerObj['eid']] .. ':0:0:0:2|t'\nend\n\nlocal toString = function(aPlayerObj)\n    local tReturn = \"\"\n    if aPlayerObj.name == \"Unknown\" then \n        local tUnit = aPlayerObj.unit\n        local tName, tRealm = UnitName(tUnit)\n        if tRealm then tRealm = string.gsub(tRealm, \"-\", \"\") end\n        if tRealm then tRealm = string.gsub(tRealm, \" \", \"\") end\n        local _, tClass = UnitClass(tUnit)\n        aPlayerObj.name = tName\n        aPlayerObj.realm = tRealm\n        aPlayerObj.class = tClass\n        if aPlayerObj.name == \"Unknown\" then \n            return \"\" \n        end\n    end\n    if _cfg.showMicroIcons then \n        tReturn = tReturn .. createIcon(aPlayerObj)\n    end\n    tReturn = tReturn .. colorize(aPlayerObj)\n    if _cfg.debug and aPlayerObj.ver ~= nil then\n        tReturn = tReturn .. \" \" .. aPlayerObj.ver\n    end\n    return tReturn .. '\\n'\nend\n\nlocal doUpdate = function()\n    local tUpdateString = \"\"\n    local tCount = 0\n    local tN = 0\n    for _, v in pairs(_ae._mainLib._party) do\n        tN = tN + 1\n        if _cfg.onlyMele then\n            if _ae.meleFilter[v.class] then\n                tUpdateString = tUpdateString .. toString(v)\n                tCount = tCount + 1\n            end\n        else\n            tUpdateString = tUpdateString .. toString(v)\n            tCount = tCount + 1\n        end\n    end\n    _ae.text = tUpdateString\n    if _cfg.showMode == 1 then \n        _ae.show = true\n    else\n        if _ae._mainLib._mhEID > 0 then\n            _ae.show = true\n        else\n            _ae.show = false\n        end    \n    end\n    if tCount == 1 then _ae.show = false end\n    if tN > 5 then \n        WeakAuras.ScanEvents('GROUP_ROSTER_UPDATE')\n    end\n    _ae.lastUpdate = _ae._mainLib.lastUpdate\nend\n\nlocal onTick = function()\n    if not _ae._mainLib then return end\n    if _ae.lastUpdate == _ae._mainLib.lastUpdate then return end\n    doUpdate()\nend\n\nlocal _triggers = {\n    -- WA_WF_NOW_TICK = onTick,\n    WA_WF_NOW_DATA_CHANGE = onTick,\n    WA_WF_NOW_LOAD = hookCore,\n}\n_ae.onTrigger = function(e, ...)\n    local fn = _triggers[e]\n    if type(fn) == 'function' then fn(...) end\n    return _ae.show\nend\n\nhookCore()\nonTick()",
