					["custom"] = "aura_env.champions = {}\naura_env.mindslayers = {}\naura_env.slayers = {}\naura_env.champion_counter = 0\naura_env.mindslayer_counter = 0        \naura_env.slayer_counter = 0\n\naura_env.lashers = {}\naura_env.wasps = {}\naura_env.stingers = {}\naura_env.lasher_counter = 0\naura_env.wasp_counter = 0        \naura_env.stinger_counter = 0\n\nfunction aura_env:registerMobByGuid(guid)\n    local type, zero, server_id, instance_id, zone_uid, npc_id, spawn_uid = strsplit(\"-\",guid)\n    if not spawn_uid then return end\n    \n    -- Guesstimate the mob quantity by using a pattern found in it's GUID\n    local spawn_counter = math.ceil(tonumber(spawn_uid:sub(1,5), 16)/8)\n    \n    \n    if npc_id == \"15252\" and not aura_env.champions[guid] then\n        \n        aura_env.champions[guid] = true\n        --            print(\"Champion-\"..guid)\n        if aura_env.champion_counter < spawn_counter then\n            aura_env.champion_counter = spawn_counter\n        end\n        \n    elseif npc_id == \"15246\" and not aura_env.mindslayers[guid] then\n        aura_env.mindslayers[guid] = true\n        --          print(\"Mindslayer-\"..guid)\n        if aura_env.mindslayer_counter < spawn_counter then\n            aura_env.mindslayer_counter = spawn_counter\n        end\n    elseif npc_id == \"15250\" and not aura_env.slayers[guid]  then\n        aura_env.slayers[guid] = true\n        --        print(\"Slayer-\"..guid)\n        if aura_env.slayer_counter < spawn_counter then\n            aura_env.slayer_counter = spawn_counter\n        end\n    elseif npc_id == \"15249\" and not aura_env.lashers[guid]  then\n        aura_env.lashers[guid] = true\n        --        print(\"Lasher-\"..guid)\n        if aura_env.lasher_counter < spawn_counter then\n            aura_env.lasher_counter = spawn_counter\n        end\n    elseif npc_id == \"15236\" and not aura_env.wasps[guid]  then\n        aura_env.wasps[guid] = true\n        --        print(\"Wasp-\"..guid)\n        if aura_env.wasp_counter < spawn_counter then\n            aura_env.wasp_counter = spawn_counter\n        end\n    elseif npc_id == \"15235\" and not aura_env.stingers[guid]  then\n        aura_env.stingers[guid] = true\n        --        print(\"Stinger-\"..guid)\n        if aura_env.stinger_counter < spawn_counter then\n            aura_env.stinger_counter = spawn_counter\n        end\n    end\nend",
