				["init"] = {
					["custom"] = "local aura_env = aura_env\naura_env.debug = false\naura_env.dummyData = false\naura_env.prefix = \"WA_StomachSync\"\nC_ChatInfo.RegisterAddonMessagePrefix(aura_env.prefix)\n\naura_env.sendUpdate = function(allstates, guid, name, currentHealth, maxHealth)\n    local _, _, _, _, _, npcid, unitid = strsplit(\"-\", guid)\n    if aura_env.mob_whitelist[tonumber(npcid)] ~= nil then\n        --Throttle message sending based on health changes\n        if currentHealth > 0 and allstates[unitid] then\n            local healthChange = allstates[unitid].value - (currentHealth / maxHealth * 100)\n            if allstates[unitid].value > 50 and healthChange < 5 then return\n            elseif allstates[unitid].value > 25 and healthChange < 2.5 then return\n            elseif healthChange < 1 then return end\n        end\n        C_ChatInfo.SendAddonMessage(\n            aura_env.prefix,\n            string.format(\"%s:%s:%s:%d:%d:%s\",\n                unitid,\n                npcid,\n                name,\n                currentHealth,\n                maxHealth,\n                GetLocale()\n            ),\n            aura_env.channel\n        )\n    end\nend\n\naura_env.mob_whitelist = {\n    [15802] = aura_env.config.enableFlesh, --Flesh Tentacle\n    [15334] = aura_env.config.enableGiant, --Giant Eye Tentacle\n    [15728] = aura_env.config.enableGiant --Giant Claw Tentacle\n}\n\nif aura_env.debug then\n    aura_env.channel = \"SAY\"\n    aura_env.mob_whitelist[3100] = aura_env.config.enableFlesh\n    aura_env.mob_whitelist[3123] = aura_env.config.enableGiant\n    aura_env.mob_whitelist[3127] = aura_env.config.enableGiant\nelse\n    aura_env.channel = \"RAID\"\nend\n\naura_env.fillDummyData = function(allstates)\n    allstates[1] = {\n        show = true,\n        changed = true,\n        progressType = \"static\",\n        value = 0,\n        total = 100,\n        index = 1,\n        lastUpdate = time(),\n        name = \"Flesh Tentacle\"\n    }\n    allstates[2] = {\n        show = true,\n        changed = true,\n        progressType = \"static\",\n        value = 25,\n        total = 100,\n        index = 1,\n        lastUpdate = time(),\n        name = \"Flesh Tentacle\"\n    }\n    allstates[3] = {\n        show = true,\n        changed = true,\n        progressType = \"static\",\n        value = 41,\n        total = 100,\n        index = 1,\n        lastUpdate = time(),\n        name = \"Giant Eye Tentacle\"\n    }\n    allstates[4] = {\n        show = true,\n        changed = true,\n        progressType = \"static\",\n        value = 100,\n        total = 100,\n        index = 1,\n        lastUpdate = time(),\n        name = \"Giant Claw Tentacle\"\n    }\nend",
