					["custom"] = "setGlobalVariables = function(...)\n    sTime, pTime, pHealth, tarName, tTillZero, dmg = ...\nend\n\nround = function(num, numDecimalPlaces)\n    return tonumber(string.format(\"%.\" .. (numDecimalPlaces or 0) .. \"f\", num))\nend\n\nformatTime = function(seconds)\n    local t = round(seconds) / 3600\n    local h = math.floor(t, 1)\n    local m = math.floor(math.fmod(t, 1) * 60)\n    local s = math.floor(math.fmod((t * 60), 1) * 60)\n    \n    return \nstring.format(\"%02d\",m)..\":\"..string.format(\"%02d\",s)\nend\n\nconditionsAreMet = function(...)\n    local callingProcedure, TargetExists, TargetIsDead, PlayerCanAttackTarget, PlayerIsAffectingCombat = ...\n    \n    if TargetExists and not\n    TargetIsDead and\n    PlayerCanAttackTarget then\n        if callingProcedure == \"getTimeTillZero\" or\n        callingProcedure == \"PLAYER_TARGET_CHANGED\" then\n            if PlayerIsAffectingCombat then\n                return true\n            else\n                return false\n            end\n        else \n            return true\n        end\n    else\n        return false\n    end\nend\n\ngetTimeTillZero = function()\n    if conditionsAreMet(\n        \"getTimeTillZero\", \n        UnitExists(\"target\"), \n        UnitIsDead(\"target\"), \n        UnitCanAttack(\"player\", \"target\"), \n        UnitAffectingCombat(\"player\")\n    ) then\n        local cHealth = UnitHealth(\"target\")\n        local hSegment = pHealth - cHealth\n        if hSegment ~= 0 then -- target has been healed or damaged\n            local cTime = GetTime()\n            local tTime = cTime - sTime\n            local tSegment = cTime - pTime\n            dmg = dmg + hSegment\n            if tSegment >= 1 then -- one or more seconds has passed; update time till zero\n                local rate = dmg / tTime\n                tTillZero = cHealth / rate\n                pTime = cTime\n            end\n            pHealth = cHealth\n        end\n    end\n    return tTillZero\nend",
