						},
						["custom"] = "function(s,event,_,subEvent, arg2, sourceGUID, sourceName)\n    if (event == \"GROUP_ROSTER_UPDATE\") or (event == \"PLAYER_ENTERING_WORLD\") or (event == \"PLAYER_TARGET_CHANGED\") or (event == \"COMBAT_LOG_EVENT_UNFILTERED\" and subEvent == \"UNIT_DIED\") or (event == \"UNIT_SPELLCAST_SUCCEEDED\") then\n        \n        -- Remove\n        for _, state in pairs(s) do\n            local unitName = state.name;\n            local isUnitInvalid = not UnitInParty(unitName)\n            \n            for i = 1, GetNumGroupMembers() do\n                if UnitName(\"raid\"..i) == unitName then\n                    local _,_,_,_,_,_,zone,online,isDead = GetRaidRosterInfo(i)\n                    if zone ~= GetRealZoneText() or not online or isDead then\n                        isUnitInvalid = true\n                    end\n                    break\n                end\n            end\n            \n            if isUnitInvalid then\n                state.show = false;\n                state.changed = true;\n            end\n        end\n        \n        -- Add        \n        for i = 1, GetNumGroupMembers() do\n            local unitName = UnitName(\"raid\"..i);\n            local unitClass = select(2,UnitClass(\"raid\"..i))\n            local _,_,_,_,_,_,zone,online,isDead = GetRaidRosterInfo(i)\n            local isBlacklisted = aura_env.blacklist[unitName]\n            local manOrder = aura_env.manualOrder[unitName]\n            local isUnitValid = (unitClass == \"DRUID\" or unitClass == \"PALADIN\" or unitClass == \"PRIEST\" or unitClass == \"SHAMAN\") and online and not isDead and not isBlacklisted and zone == GetRealZoneText()\n            if aura_env.config['groupSortType'] == 2 then -- 1. Alphabetical, 2. Manual\n                isUnitValid =  manOrder and isUnitValid\n            end\n            \n            if isUnitValid then\n                s[unitName] = {\n                    show = true,\n                    changed = true, \n                    progressType = \"timed\",\n                    duration = aura_env.savedDuration[unitName] or 0,\n                    expirationTime = aura_env.savedExpiration[unitName] or 0,\n                    name = unitName,    \n                    rdyTimer = aura_env.savedExpiration[unitName] or 0,\n                    class = unitClass,\n                    sortType = aura_env.config['groupSortType'],\n                    orderNum = manOrder,\n                }\n            end\n        end\n        \n        return true;\n    elseif event == \"UNIT_AURA\" then\n        for i = 1, GetNumGroupMembers() do\n            local playerClass = select(2,UnitClass(\"raid\"..i))\n            if playerClass == \"PRIEST\" or playerClass == \"PALADIN\" or playerClass == \"DRUID\" or playerClass == \"SHAMAN\" then\n                local debuffFound = false\n                local playerName = UnitName(\"raid\"..i)\n                \n                for j = 1, 16 do\n                    local debuffID = select(10,UnitDebuff(\"raid\"..i, j))\n                    if debuffID == 29184 or debuffID == 29197 or debuffID == 29195 or debuffID == 29199 then\n                        local t = GetTime()\n                        debuffFound = true\n                        \n                        if s[playerName] ~= nil and s[playerName]['rdyTimer'] - t <= 0 then\n                            s[playerName]['duration'] = aura_env.debuffDuration\n                            s[playerName]['expirationTime'] = t + aura_env.debuffDuration\n                            s[playerName]['name'] = playerName\n                            s[playerName]['rdyTimer'] = t + aura_env.debuffDuration\n                            s[playerName]['class'] = playerClass\n                            s[playerName]['sortType'] = aura_env.config['groupSortType']\n                            s[playerName]['orderNum'] = aura_env.manualOrder[playerName]\n                            aura_env.savedDuration[playerName] = s[playerName].duration;\n                            aura_env.savedExpiration[playerName] = s[playerName].expirationTime;\n                            \n                            -- Whispers the next player in line that they're doing the next heal\n                            if aura_env.config['whisperNextHealerBool'] == true then\n                                if playerName == aura_env.playerName then\n                                    local playersTable = {}\n                                    local nextPlayerIndex = 0\n                                    \n                                    for k,v in pairs(s) do\n                                        if v ~= nil then\n                                            table.insert(playersTable, v.name)\n                                        end\n                                    end\n                                    \n                                    -- Selected Sorting - Terrible and needs refactoring\n                                    if aura_env.config['groupSortType'] == 1 then -- 1. Alphabetical, 2. Manual\n                                        table.sort(playersTable)\n                                    elseif aura_env.config['groupSortType'] == 2 then -- 1. Alphabetical, 2. Manual\n                                        local manTableUnsorted = {}\n                                        for i = 1, #playersTable do\n                                            manTableUnsorted[aura_env.manualOrder[playersTable[i]]] = playersTable[i]\n                                        end\n                                        local manTableSorted = {}\n                                        for k, v in pairs(manTableUnsorted) do\n                                            table.insert(manTableSorted, {k,v})\n                                        end\n                                        table.sort(manTableSorted, function(a,b) return a[1] < b[1] end)\n                                        local manTableNamesSorted = {}\n                                        for _, v in ipairs(manTableSorted) do\n                                            table.insert(manTableNamesSorted, v[2])\n                                        end\n                                        playersTable = manTableNamesSorted\n                                    end\n                                    \n                                    for i = 1, #playersTable do\n                                        if playersTable[i] == playerName then\n                                            if i == #playersTable then\n                                                nextPlayerIndex = 1\n                                            else\n                                                nextPlayerIndex = i + 1\n                                            end \n                                            \n                                            if playersTable[nextPlayerIndex] ~= playerName then SendChatMessage(\"YOU'RE HEALING NEXT\", \"WHISPER\", nil, playersTable[nextPlayerIndex]) end\n                                            break;\n                                        end\n                                    end\n                                end\n                            end\n                            return true;\n                        end\n                    end \n                end\n                \n                if not debuffFound and (s[playerName] ~= nil and s[playerName]['rdyTimer'] - GetTime() < 0) then\n                    s[playerName]['rdyTimer'] = -1 -- we set this to something less than 0 so that the condition that's been set in the Conditions tab will un-toggle\n                end\n            end\n        end\n    end\nend",
