			["customText"] = "function()\n    if aura_env.states[1] and aura_env.states[1].unitCaster then\n        if (not aura_env.lastCaster or aura_env.states[1].unitcaster ~= aura_env.lastCaster) or (not aura_env.last or GetTime() - aura_env.last > aura_env.config.throttle) then\n            aura_env.last = GetTime()\n            aura_env.lastCaster = aura_env.states[1].unitCaster\n            local unit = aura_env.states[1].unitCaster\n            local isTanking, status, scaledPercentage, rawPercentage, threatValue = UnitDetailedThreatSituation(unit, \"target\")\n            scaledPercentage = Round(scaledPercentage or 0)\n            if threatValue ~= nil then\n                if isTanking then\n                    aura_env.percent = 100\n                    aura_env.delta = 0                \n                else\n                    aura_env.percent = scaledPercentage\n                    aura_env.delta = (threatValue * 100 / scaledPercentage) - threatValue\n                end\n                --aura_env.percent = threatPercent - threatPercent%1.0\n                -- Normalize threat such that 1 dmg = 1 threat\n                aura_env.delta = math.floor((aura_env.delta - aura_env.delta%1) / 100)\n                \n                local colorRed = \"FFFF3333\"\n                local colorYellow = \"fffff569\"\n                local colorGreen = \"FF42FF33\"\n                local color\n                if aura_env.percent < 70 then\n                    color = colorGreen\n                elseif aura_env.percent >= 70 and aura_env.percent < 90 then\n                    color = colorYellow\n                elseif aura_env.percent >= 90 then\n                    color = colorRed\n                end\n                \n                return WrapTextInColorCode(FormatLargeNumber(aura_env.delta), color), WrapTextInColorCode(aura_env.percent, color)\n            else\n                aura_env.percent = 0\n                aura_env.delta = 0\n                return aura_env.delta, aura_env.percent\n            end\n        end\n    end\nend",
