   [15630] = {moon, diamond, circle}, -- Fankriss worms\n    [15240] = {skull, cross, square, moon}, -- Hive Crawlers (Scorpions)\n    [15235] = {cross}, -- Vekniss Stinger\n    [15236] = none, -- Vekniss Wasp\n    [15249] = {skull}, -- Qiraji Lasher\n    [15316] = none, -- Qiraji Scarab\n    [15252] = {square}, -- Qiraji Champion\n    [15246] = {triangle, moon, cross, skull}, -- Qiraji Mindslayer\n    [15250] = {skull, cross, moon, triangle}, -- Qiraji Slayer\n    [15311] = {square}, -- Anubisath Warder\n    [15312] = main, --Obsidian Nullifier\n    \n    -- Blackwing Lair\n    -- Post Vael Death Talon Guard\n    [12467] = {circle}, -- Captain\n    [12464] = {skull, cross, moon}, -- Seether\n    [12465] = {diamond, star}, -- Wyrmguard (sleep)\n    [12463] = {moon, cross, triangle}, --  Flamescale\n    -- Suppression Room\n    [12468] = {skull}, -- Hatcher\n    [12458] = {moon, cross, square}, -- Taskmaster\n    -- Drake Hallway\n    [12459] = main, -- Warlocks\n    [12457] = {moon, triangle}, -- spellbinder\n    [12461] = {square, moon, skull}, -- overseer\n    [12460] = {triangle, diamond, cross}, -- wyrmguard\n    [13996] = none, -- Blackwing Technician\n    [14025] = none, -- Corrupted Bronze Whelp\n    [14022] = none, -- Corrupted Red Whelp\n    [14023] = none, -- Corrupted Green Whelp\n    [14024] = none, -- Corrupted Blue Whelp\n    \n    -- molten core\n    [11673] = other, -- ancient corehound\n    [11671] = all, -- core rager (hound packs\n    [11672] = other, -- core hound\n    [12099] = all, -- Firesworn (garr adds)\n    [11668] = other, -- firelord\n    [11666] = {skull}, -- fire walker (blossom\n    [11667] = {cross}, -- flameguard\n    [11661] = main, -- gehennas adds\n    [11664] = group, -- domo elites\n    [11663] = {1,3,4,2}, -- domo healers\n    [11662] = group, -- sulfuron adds\n    [12119] = main, -- lucifron adds\n    [11665] = other, -- annihilator\n    [12076] = other, -- lava elemental\n    [12100] = other, -- lava reaver\n    [12101] = other, -- surger\n    [11659] = main, -- molten destroyer\n    [11658] = main, -- molten giant\n    \n    [8724] = main, -- debug, org auctioneer\n    [8673] = main, -- debug, org auctioneer\n    [10685] = all_ordered, -- debug, swine\n    [3100] = all_ordered, -- debug, elder mottled boar\n}\n\n\n-- don't modify below unless you know what you're doing!\nC_ChatInfo.RegisterAddonMessagePrefix(\"ORHWA_markers\")\n\naura_env.timeout = {0,0,0,0,0,0,0,0}\naura_env.guidTimeout = {0}\n\naura_env.markPlayers = function()\n    if InCombatLockdown() or not aura_env.config.autoplayers then return end\n    if aura_env.config.star then SetRaidTarget(aura_env.config.star, 1) end\n    if aura_env.config.circle then SetRaidTarget(aura_env.config.circle, 2) end\n    if aura_env.config.diamond then SetRaidTarget(aura_env.config.diamond, 3) end\n    if aura_env.config.triangle then SetRaidTarget(aura_env.config.triangle, 4) end    \nend\n\naura_env.markMouse = function()\n    if not IsShiftKeyDown() then return end\n    local gid = UnitGUID(\"mouseover\")\n    local now = time()\n    local currMark = GetRaidTargetIndex(\"mouseover\")\n    local altDown = IsAltKeyDown()\n    local ctrlDown = IsControlKeyDown()\n    if altDown and UnitExists(\"mouseover\") then\n        if ctrlDown then\n            for i=1,8 do\n                SetRaidTargetIcon(\"mouseover\", i)\n                aura_env.timeout[i] = now-1\n            end\n            SetRaidTargetIcon(\"mouseover\", 8)\n            aura_env.timeout[8] = now-2\n            aura_env.guidTimeout[gid] = now-2\n        elseif currMark then\n            SetRaidTargetIcon(\"mouseover\", currMark)\n            aura_env.timeout[currMark] = now-2\n            aura_env.guidTimeout[gid] = now-2\n        end\n    end\n    if currMark or altDown then return end\n    \n    if UnitIsPlayer(\"mouseover\") and aura_env.config['markPlayers'] then\n        if aura_env.config.star == UnitName(\"mouseover\") then SetRaidTarget(aura_env.config.star, 1) end\n        if aura_env.config.circle == UnitName(\"mouseover\") then SetRaidTarget(aura_env.config.circle, 2) end\n        if aura_env.config.diamond == UnitName(\"mouseover\") then SetRaidTarget(aura_env.config.diamond, 3) end\n        if aura_env.config.triangle == UnitName(\"mouseover\") then SetRaidTarget(aura_env.config.triangle, 4) end\n        return\n    end\n    \n    local id = aura_env.UnitId(gid)\n    local canAttack = UnitCanAttack(\"player\", \"mouseover\")\n    local isDead = UnitIsDead(\"mouseover\")\n    local creatureType = UnitCreatureType(\"mouseover\")\n    local inCombat = UnitAffectingCombat(\"mouseover\")\n    if not id or not canAttack or creatureType == 'Critter' or isDead or (inCombat and not ctrlDown) then return end\n    local markers = aura_env.targets[id]\n    if not markers then\n        markers = aura_env.targets[3100]\n    end\n    \n    for i, icon in ipairs(markers) do\n        local timeout = 0\n        if aura_env.config['markTimeout'] then\n            timeout = aura_env.config['markTimeout']\n        end\n        if ( not aura_env.timeout[icon] or aura_env.timeout[icon] < ( now-1 ) )\n        and ( not aura_env.guidTimeout[gid] or aura_env.guidTimeout[gid] < ( now-1 ) ) then\n            aura_env.timeout[icon] = now+timeout\n            aura_env.guidTimeout[gid] = now+timeout\n            SetRaidTarget(\"mouseover\", icon)\n            return\n        end\n    end    \nend\n\naura_env.resetTimeouts = function()\n    for i=1,8 do\n        aura_env.timeout[i] = time()-2\n    end\nend\n\n\naura_env.UnitId = function(guid)\n    if not guid or type(guid) ~= \"string\" then return end\n    local unitType, _, _, _, _, npcID = strsplit(\"-\", guid)\n    if unitType ~= \"Player\" then\n        return tonumber(npcID)\n    end \nend\n\n\n\n\n\n",
