			["actions"] = {
				["start"] = {
					["message"] = "",
					["custom"] = "aura_env.uwuDMTbs_RefreshTimers()\naura_env.CheckLootMethod()\n\nif aura_env.config.silentmode and aura_env.SoundArray[\"Sound_EnableAllSound\"] == nil then \n    aura_env.SoundArray[\"Sound_EnableAllSound\"] =  GetCVar(\"Sound_EnableAllSound\")             \n    aura_env.SoundArray[\"Sound_EnableSFX\"] =  GetCVar(\"Sound_EnableSFX\")   \n    aura_env.SoundArray[\"Sound_EnableMusic\"] =  GetCVar(\"Sound_EnableMusic\")   \n    aura_env.SoundArray[\"Sound_EnableAmbience\"] =  GetCVar(\"Sound_EnableAmbience\")\n    aura_env.SoundArray[\"Sound_EnableDialog\"] = GetCVar(\"Sound_EnableDialog\") \n    aura_env.SoundArray[\"Sound_EnableSoundWhenGameIsInBG\"] = GetCVar(\"Sound_EnableSoundWhenGameIsInBG\")\n    SetCVar(\"Sound_EnableAllSound\",1) \n    SetCVar(\"Sound_EnableSFX\",0) \n    SetCVar(\"Sound_EnableMusic\",0) \n    SetCVar(\"Sound_EnableAmbience\",0) \n    SetCVar(\"Sound_EnableDialog\",0) \n    SetCVar(\"Sound_EnableSoundWhenGameIsInBG\",1)   \nend\n\nif aura_env.config.setbgfps then \n    aura_env.bgfps = GetCVar(\"maxfpsbk\")\n    SetCVar(\"maxfpsbk\",8)\nend",
					["message_type"] = "EMOTE",
					["do_message"] = false,
					["do_custom"] = true,
				},
				["init"] = {
					["custom"] = "aura_env.DMTBlackListNames = {} \n-- -- -- -- -- - - example = {\"Pinapplepizzalover-Herod\",\"Pinapplepizzahater-Herod\"} \n-- Add people who wont receive automatic invites. You must add your realm name.\n\naura_env.DMTKingBuffOwners = {}\naura_env.uwuDMTbs_HearthTime = {}\naura_env.SoundArray = {}\naura_env.bgfps = nil     \n\naura_env.loottable = {     \n    \"none\",\n    \"freeforall\",\n    \"group\",\n    \"master\",\n    \"needbeforegreed\",\n    \"roundrobin\"\n}  \naura_env.dummy = 0 \n\nif not aura_env.buffssold then aura_env.buffssold = 0 end\n\n\naura_env.uwuDMTbs_PlaySound =  function(arg)\n    if arg == 1 then -- respawn sound\n        PlaySound(8959, \"Master\")\n    elseif arg == 2 then -- trade sound\n        PlaySound(8959, \"Master\")\n    elseif arg == 3 then -- logout sound\n        PlaySound(8959, \"Master\")\n    end\n    --[[ or you can use something like this\n\n    PlaySoundFile(\"Sound\\\\Music\\\\ZoneMusic\\\\DMF_L70ETC01.mp3\", \"Master\") \n\n    PlaySoundFile(\"Interface\\\\AddOns\\\\MyAddOn\\\\mysound.ogg\", \"Master\")\n\n----]]\nend\n\n\naura_env.uwuDMTbs_CheckAltTab = function() \n    return GetFramerate() < GetCVar(\"maxfpsbk\")+1  \nend\n\naura_env.DMTCheckName = function(name)\n    for i=1, #aura_env.DMTBlackListNames do\n        if name == aura_env.DMTBlackListNames[i] then return false end\n    end\n    return true\nend\n\naura_env.uwuDMTbs_RefreshTimers = function()\n    if aura_env.config.enabletimers then\n        local Clock = time() \n        if WeakAurasSaved.uwuDMTbs_R1 and Clock - WeakAurasSaved.uwuDMTbs_R1 < 7200 then\n            local dur = GetTime() + 7200 - Clock + WeakAurasSaved.uwuDMTbs_R1\n            WeakAuras.ScanEvents(\"uwuDMTbs_R1Event\", true, dur) \n        end\n        if WeakAurasSaved.uwuDMTbs_R2 and Clock - WeakAurasSaved.uwuDMTbs_R2 < 7200 then\n            \n            local dur = GetTime() + 7200 - Clock + WeakAurasSaved.uwuDMTbs_R2\n            WeakAuras.ScanEvents(\"uwuDMTbs_R2Event\", true, dur)   \n        end\n        if WeakAurasSaved.uwuDMTbs_SW and Clock - WeakAurasSaved.uwuDMTbs_SW < 7200 then\n            \n            local dur = GetTime() + 7200 - Clock + WeakAurasSaved.uwuDMTbs_SW\n            WeakAuras.ScanEvents(\"uwuDMTbs_SWEvent\", true, dur)   \n        end\n    end\nend\n\nif aura_env.config.forceresetswarm then\n    if WeakAurasSaved.uwuDMTbs_SW then\n        WeakAurasSaved.uwuDMTbs_SW=nil\n        print(\"uwuDMTbs: Swarmer timer has been reset!\")\n    end\nend\n\nif aura_env.config.forceresetreav then\n    if WeakAurasSaved.uwuDMTbs_R1 or WeakAurasSaved.uwuDMTbs_R2 then\n        print(\"uwuDMTbs: Reavers timers have been reset!\")\n        WeakAurasSaved.uwuDMTbs_R1=nil\n        WeakAurasSaved.uwuDMTbs_R2=nil\n    end\nend\n\naura_env.CheckBgFps = function()\n    if aura_env.bgfps ~= nil and aura_env.config.setbgfps then SetCVar(\"maxfpsbk\",aura_env.bgfps)aura_env.bgfps = nil  end\nend\n\naura_env.CheckLootMethod = function()\n    if aura_env.config.LootRules ~= 1 and not UnitInBattleground(\"player\") and UnitIsGroupLeader(\"player\") and GetLootMethod() ~= aura_env.loottable[aura_env.config.LootRules] then\n        if aura_env.config.LootRules ~= 4 then\n            SetLootMethod(aura_env.loottable[aura_env.config.LootRules]) \n        else\n            SetLootMethod(aura_env.loottable[aura_env.config.LootRules], \"player\") \n        end \n    end\nend\n\naura_env.CheckSound = function()\n    if aura_env.SoundArray[\"Sound_EnableAllSound\"] ~= nil then \n        SetCVar(\"Sound_EnableAllSound\",aura_env.SoundArray[\"Sound_EnableAllSound\"]) \n        SetCVar(\"Sound_EnableSFX\",aura_env.SoundArray[\"Sound_EnableSFX\"]) \n        SetCVar(\"Sound_EnableMusic\",aura_env.SoundArray[\"Sound_EnableMusic\"]) \n        SetCVar(\"Sound_EnableAmbience\",aura_env.SoundArray[\"Sound_EnableAmbience\"]) \n        SetCVar(\"Sound_EnableDialog\",aura_env.SoundArray[\"Sound_EnableDialog\"]) \n        SetCVar(\"Sound_EnableSoundWhenGameIsInBG\",aura_env.SoundArray[\"Sound_EnableSoundWhenGameIsInBG\"])  \n        aura_env.SoundArray = {}\n    end\nend",
