						["custom"] = "function (e, msg, author, lang, channelName)\n    if channelName then channelName = string.sub(channelName,1,1) end\n    if msg then\n        msg = string.lower(msg)\n        if \n        \n        aura_env.config.ChatScan and e == \"CHAT_MSG_CHANNEL\" and channelName and string.find(aura_env.config.ChatScanChannels, channelName) \n        and \n        (\n            msg == \"wtb dmt\"\n            or \n            ( \n                string.find(msg, 'wtb') \n                or \n                (\n                    channelName==1 \n                    and \n                    ( \n                        string.find(msg, 'lf ')==1 \n                        or \n                        string.find(msg, ' lf ') \n                        or\n                        string.find(msg, 'anyone selling') \n                        or \n                        string.find(msg, 'anybody selling')\n                        or \n                        string.find(msg, 'any1 selling') \n                    ) \n                ) \n            ) \n            and \n            (\n                string.find(msg, 'buffs') \n                or \n                string.find(msg, 'boofs')\n            )\n        )\n        or aura_env.config.autoinv and e == \"CHAT_MSG_WHISPER\" and string.find(msg, 'inv')\n        \n        then\n            if aura_env.DMTCheckName(author) then\n                InviteUnit(author)\n            end\n        end\n    end       \nend             \n\n",
