						["custom"] = "function (e,...)\n    if aura_env.config.raidmarks and e == \"UPDATE_MOUSEOVER_UNIT\" and not UnitInBattleground(\"player\") then\n        local guid = UnitGUID(\"mouseover\")\n        if guid then\n            local npcid = select(6, strsplit(\"-\",guid)) \n            npcid = tonumber(npcid)\n            if UnitCanAttack(\"player\", \"mouseover\")  then\n                if npcid == 11441 then\n                    if GetRaidTargetIndex(\"mouseover\") ~=7 then -- Gordok Brute\n                        SetRaidTarget(\"mouseover\",7)\n                    end\n                elseif npcid == 11444 then \n                    if GetRaidTargetIndex(\"mouseover\") ~=8 then -- Mage-Lord\n                        SetRaidTarget(\"mouseover\",8)\n                    end\n                elseif npcid == 13036 then \n                    if GetRaidTargetIndex(\"mouseover\") ~=5 then -- Mastiff\n                        SetRaidTarget(\"mouseover\",5)\n                    end\n                end\n            end\n            if npcid == 14321 then \n                if GetRaidTargetIndex(\"mouseover\") ~=4 then  -- Fengus\n                    SetRaidTarget(\"mouseover\",4)\n                end\n            elseif npcid == 14323 then \n                if GetRaidTargetIndex(\"mouseover\") ~=6 then  -- Slip'kik\n                    SetRaidTarget(\"mouseover\",6)\n                end\n            elseif npcid == 14325 then \n                if GetRaidTargetIndex(\"mouseover\") ~=3 then  -- KromCrush\n                    SetRaidTarget(\"mouseover\",3)\n                end\n            end\n        end\n    elseif e == \"CHAT_MSG_SYSTEM\" then \n        local arg = ...\n        if arg then\n            if string.match(arg, string.gsub(INSTANCE_RESET_SUCCESS, \"%%s\", \"(.+)\"))  then \n                WeakAurasSaved.uwuDMTbs_R1=nil\n                WeakAurasSaved.uwuDMTbs_R2=nil\n                WeakAurasSaved.uwuDMTbs_SW=nil\n                WeakAuras.ScanEvents(\"uwuDMTbs_R1Event\")  \n                WeakAuras.ScanEvents(\"uwuDMTbs_R2Event\")  \n                WeakAuras.ScanEvents(\"uwuDMTbs_SWEvent\")  \n            end                                                \n            local name = string.match(arg, string.gsub(ERR_JOINED_GROUP_S, \"%%s\", \"(.+)\")) or string.match(arg, string.gsub(ERR_RAID_MEMBER_ADDED_S , \"%%s\", \"(.+)\"))      \n            if name then -- someone joined the group \n                if not UnitInRaid(\"player\") then ConvertToRaid()end \n                aura_env.CheckLootMethod()\n                if \n                aura_env.DMTCheckName(string.lower(name))\n                and \n                aura_env.config.welcome \n                and \n                aura_env.config.welcomestring\n                and \n                (\n                    UnitIsGroupLeader(\"player\") \n                    or \n                    UnitInBattleground(\"player\")\n                    and \n                    IsInGroup(LE_PARTY_CATEGORY_HOME)\n                )  then\n                    SendChatMessage(aura_env.config.welcomestring ,\"WHISPER\" ,GetDefaultLanguage(\"player\") ,name) \n                end\n            else                        \n                name = string.match(arg, string.gsub(ERR_LEFT_GROUP_S, \"%%s\", \"(.+)\")) or string.match(arg, string.gsub(ERR_RAID_MEMBER_REMOVED_S , \"%%s\", \"(.+)\"))     \n                if name then -- someone left the group\n                  if aura_env.DMTKingBuffOwners[name] and aura_env.config.hearthtimers then\n                      aura_env.DMTKingBuffOwners[name]=nil\n                      for i=1,5 do\n                          local Clock = time()\n                          if not aura_env.uwuDMTbs_HearthTime[i] or Clock - aura_env.uwuDMTbs_HearthTime[i]>63 then\n                              aura_env.uwuDMTbs_HearthTime[i]= Clock\n                              WeakAuras.ScanEvents(\"uwuDMTbs_HearthEvent_\"..i)\n                              break\n                          end \n                      end\n                  end\n                end\n            end \n        end\n    elseif e == \"TRADE_SHOW\" and aura_env.uwuDMTbs_CheckAltTab() then\n        local name = GetUnitName(\"npc\")\n        if aura_env.config.swh_trade then print(\"Gold from \"..name..\" aint gonna grab itself!!1\") FlashClientIcon() end\n        if aura_env.config.snd_trade then aura_env.uwuDMTbs_PlaySound(2) end\n    elseif e == \"PLAYER_CAMPING\" and aura_env.uwuDMTbs_CheckAltTab()then\n        aura_env.uwuDMTbs_PlaySound(3)\n    elseif e == \"PLAYER_LOGOUT\" then      \n        aura_env.CheckSound()\n        aura_env.CheckBgFps()\n    elseif e == \"uwuDMTbs_CheckTimer\" then\n        aura_env.uwuDMTbs_RefreshTimers()\n    elseif e == \"uwuDMTbs_SelfWhisper\" then\n        local a = ...\n        if aura_env.uwuDMTbs_CheckAltTab() then\n            if aura_env.config.swh_timer then\n                FlashClientIcon() \n            end\n            if aura_env.config.snd_timer then\n                aura_env.uwuDMTbs_PlaySound(1)\n            end\n        end\n        if UnitIsGroupLeader(\"player\")or UnitInBattleground(\"player\")and IsInGroup(LE_PARTY_CATEGORY_HOME) then \n            if IsInRaid(LE_PARTY_CATEGORY_HOME) then \n                if not UnitInBattleground(\"player\")then\n                    SendChatMessage(a ,\"RAID_WARNING\");\n                else\n                    SendChatMessage(a ,\"RAID\");\n                end\n            elseif IsInGroup(LE_PARTY_CATEGORY_HOME) then\n                SendChatMessage(a,\"PARTY\");\n            else\n                print(a) \n            end\n        end\n    end  \nend",
