llType == 1 ) then\n        print(\"Rolled \" .. aura_env.cGreen .. \"NEED|r on \" .. itemLink); \n    elseif (rollType == 2 ) then\n        print(\"Rolled \" .. aura_env.cYellow .. \"GREED|r on \" .. itemLink);         \n    end   \nend\n\naura_env.processLootRoll = function(rollId)\n    \n    local rollInfo = {GetLootRollItemInfo(rollId)};\n    local rollItemLink = GetLootRollItemLink(rollId);\n    local rollItemId = aura_env.getItemIdFromItemLink(rollItemLink);\n    \n    local isCoin = aura_env.isCoin(rollItemId);\n    local isBijou = aura_env.isBijou(rollItemId);\n    \n    if ( not isCoin and not isBijou ) then\n        return false;\n    end\n    \n    local rollConfig = nil;        \n    if ( isCoin ) then\n        rollConfig = aura_env.config.coins.roll;\n    else \n        rollConfig = aura_env.config.bijous.roll;\n    end\n    \n    local rollType = aura_env.getCoinRollTypeForRollConfig(rollConfig);\n    if ( rollType ~= nil ) then\n        \n        RollOnLoot(rollId, rollType);\n        aura_env.notifyAboutRoll(rollType, rollItemLink);\n    end  \n    \nend\n\naura_env.printSetting = function(text) \n    aura_env.message(aura_env.cSettings .. aura_env.auraName .. \"|r: \" .. text);\nend\n\naura_env.printSettings = function() \n    \n    aura_env.printSetting(\"Bijous Roll: \" .. \n        aura_env.getColoredRollTextForRollConfig(aura_env.config.bijous.roll));\n    aura_env.printSetting(\"Coins Roll: \" .. \n        aura_env.getColoredRollTextForRollConfig(aura_env.config.coins.roll));    \nend\n\naura_env.isInZgZone = function() \n    \n    local zoneId = select(8, GetInstanceInfo());\n    return zoneId == 309;\nend",
