			["displayText"] = "%n",
			["customText"] = "function()\n    if (aura_env.text ~= nil) then\n        return aura_env.text; \n    end\n    return \"\"; \nend",
			["yOffset"] = 420.8560791015625,
			["anchorPoint"] = "CENTER",
