			["actions"] = {
				["start"] = {
				},
				["finish"] = {
				},
				["init"] = {
					["custom"] = "-- Name Blacklist\nlocal blacklist = {\n    [\"Coinflip\"] = true,\n}\n\n-- Don't Edit Below\naura_env.lastRun = nil;\naura_env.text = \"\";\n\naura_env.unitQualifies = function(unit)\n    local _, powerType = UnitPowerType(unit);\n    local isMana = \"MANA\" == powerType;\n    \n    local _, unitClass = UnitClass(unit);\n    local isHealerClass = unitClass == \"PALADIN\" or unitClass == \"DRUID\" or unitClass == \"PRIEST\" or unitClass == \"SHAMAN\";\n    \n    local isNotBlacklisted = blacklist[UnitName(unit)] ~= true;\n    \n    return isMana and isHealerClass and isNotBlacklisted;\nend\n\naura_env.getUnitManaPercent = function(unit)\n    return (UnitPower(unit) / UnitPowerMax(unit)) * 100;\nend\n\naura_env.getAverageManaPercent = function(percentTable)\n    local amountOfPlayers = 0;\n    local totalPercent = 0;\n    for _, percent in pairs(percentTable) do\n        amountOfPlayers = amountOfPlayers + 1;\n        totalPercent = totalPercent + percent;\n    end\n    \n    if (amountOfPlayers == 0) then\n        return nil; \n    end\n    \n    return totalPercent / amountOfPlayers;\nend\n\naura_env.createPercentTable = function()\n    local percentTable = {};\n    for unit in WA_IterateGroupMembers() do\n        if (aura_env.unitQualifies(unit)) then\n            percentTable[unit] = aura_env.getUnitManaPercent(unit);\n        end\n    end\n    \n    return percentTable;\nend\n\naura_env.createSortedTable = function(percentTable)\n    local sortTable = {};\n    \n    -- Ascending Order Sort\n    local sortFunction = function(durationA, durationB)\n        return durationA < durationB;\n    end\n    \n    -- Iterate Though Table, Build New Table\n    for unit in pairs(percentTable) do\n        table.insert(sortTable, unit);\n    end\n    \n    -- Sort New Table, Order For Key, GUID For Value\n    table.sort(sortTable, function(a, b)\n            return sortFunction(percentTable[a], percentTable[b]);\n    end)\n    \n    return sortTable;\nend\n\naura_env.getManaColor = function(manaPercent)\n    local color = nil;\n    if (manaPercent >= aura_env.config.highManaThreshold) then\n        color = aura_env.config.highManaColor;\n    elseif (manaPercent < aura_env.config.highManaThreshold and manaPercent >= aura_env.config.mediumManaThreshold) then\n        color = aura_env.config.mediumManaColor;\n    else\n        color = aura_env.config.lowManaColor;\n    end\n    \n    return CreateColor(color[1], color[2], color[3], color[4]);\nend\n\naura_env.createDisplayText = function(sortedTable, percentTable)\n    local str = \"\";\n    local averageManaPercent = aura_env.getAverageManaPercent(percentTable);\n    \n    if (averageManaPercent ~= nil and aura_env.config.average) then\n        local averageManaPercentStr = string.format(\"%.1f%%\", averageManaPercent);\n        if (aura_env.config.colorAverageMana) then\n            local color = aura_env.getManaColor(averageManaPercent);\n            averageManaPercentStr = color:WrapTextInColorCode(averageManaPercentStr);\n        end\n        \n        str = string.format(\"Average Mana: %s\\n\", averageManaPercentStr);\n    end\n    \n    if (aura_env.config.individual) then\n        for _, unit in ipairs(sortedTable) do\n            local rawManaPercent = percentTable[unit];\n            local manaPercent = string.format(\"%.1f%%\", rawManaPercent);\n            if (aura_env.config.colorIndividualMana) then\n                local color = aura_env.getManaColor(rawManaPercent);\n                manaPercent = color:WrapTextInColorCode(manaPercent);\n            end\n            \n            str = str .. string.format(\"%s: %s\", WA_ClassColorName(unit), manaPercent) .. \"\\n\";\n        end\n    end\n    \n    return str;\nend",
