						["custom"] = "function(allstates, event, ...)\n    if event == \"GROUP_ROSTER_UPDATE\" or event == \"PLAYER_ENTERING_WORLD\" then\n        for _, state in pairs(allstates) do\n            state.show = false\n            state.changed = true\n        end\n        for unit in WA_IterateGroupMembers() do\n            local unitName = UnitName(unit)\n            local class = UnitClass(unit)\n            local factionName = UnitFactionGroup(\"player\")\n            for _, v in ipairs(aura_env.spells) do\n                if class == v.class then\n                    if factionName == \"Horde\" and v.name == \"Heroism\" then\n                        v.name = \"Bloodlust\"\n                        v.icon = 136012\n                    end\n                    local isUnitValid = (UnitLevel(unitName) == 70 and v.enabled == true)\n                    if isUnitValid == true then\n                        allstates[unitName..v.name] = {\n                            show = true,\n                            changed = true,\n                            progressType = \"timed\",\n                            duration = aura_env.savedDuration[unitName..v.name] or 0,\n                            expirationTime = aura_env.savedExpiration[unitName..v.name] or 0,\n                            name = unitName,\n                            icon = v.icon,\n                            index = v.name,\n                            rdyTimer = aura_env.savedExpiration[unitName..v.name] or 0,\n                        }\n                    end\n                end\n            end\n        end\n    end\n    if IsInGroup() == true then\n        local _, subEvent, _, guid, sourceName, _, _, destGUID, destName, _, _, spellId, spellName, missType = ...\n        if subEvent == \"SPELL_CAST_SUCCESS\" and allstates[sourceName..spellName] ~= nil then\n            local timestamp = GetTime()\n            for _, v in ipairs(aura_env.spells) do\n                if (spellName == v.name and v.enabled == 