						["custom"] = "function(s, event, timestamp, subEvent, ...)   \n    \n    --aura_env.debug_print = function(group,msg,...)\n    --    return WeakAuras.ScanEvents(\"DPRINT\",group,aura_env.id,msg,...)\n    --end\n    --aura_env.debug_print(\"debug\", \"desc\")\n    \n    local sourceName = select(3, ...)\n    \n    -- Check if unit should be tracked\n    local function isUnitValid(uName)\n        if (UnitClass(uName) == \"Warrior\") or (UnitClass(uName) == \"Druid\") then\n            local mt = false\n            for i=1,GetNumGroupMembers(),1 do\n                local rName, _, _, _, _, _, _, rOnline, rDead, rRole = GetRaidRosterInfo(i)\n                if (uName == rName) and (rRole == \"MAINTANK\") and (rDead == false) and (rOnline == true) then\n                    return true;\n                end\n            end\n            if (mt == false) then\n                return false;\n            end\n        end\n        return false;\n    end\n    \n    -- Set success status\n    local function setStatus (ss)\n        s[sourceName] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = s[sourceName].duration,\n            expirationTime = s[sourceName].expirationTime,\n            name = sourceName,    \n            index = s[sourceName].index,\n            rdyTimer = s[sourceName].rdyTimer,\n            successStatus = ss,    \n            aggro = s[sourceName].aggro,\n            debuff = s[sourceName].debuff,\n        }\n    end\n    \n    -- Set aggro status\n    local function setAggro (ag)\n        s[sourceName] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = s[sourceName].duration,\n            expirationTime = s[sourceName].expirationTime,\n            name = sourceName,    \n            index = s[sourceName].index,\n            rdyTimer = s[sourceName].rdyTimer,\n            successStatus = s[sourceName].successStatus,    \n            aggro = ag,\n            debuff = s[sourceName].debuff,\n        }\n    end\n    \n    -- Update tank threat\n    local function updateThreat()\n        local isTanking =UnitDetailedThreatSituation(sourceName, sourceName..\"-target\")\n        if (s[sourceName].successStatus >= 1) and (s[sourceName].rdyTimer == 0) then\n            setStatus(0)\n        end\n        if isTanking then\n            setAggro(1)\n        end\n        if not isTanking then\n            setAggro(0)\n        end\n    end\n    \n    -- Build list of tanks in raid\n    if (event == \"GROUP_ROSTER_UPDATE\") or (event == \"PLAYER_ENTERING_WORLD\") or (event == \"PLAYER_TARGET_CHANGED\") then        \n        -- Remove player\n        for _, state in pairs(s) do\n            local unitName = state.name;\n            \n            if not isUnitValid(unitName) then\n                state.show = false;\n                state.changed = true;\n            end\n        end    \n        -- Add Player\n        for unit in WA_IterateGroupMembers(true) do           \n            local unitName = UnitName(unit);\n            if isUnitValid(unitName) == true then\n                s[unitName] = {\n                    show = true,\n                    changed = true, \n                    progressType = \"timed\",\n                    duration = aura_env.savedDuration[unitName] or 0,\n                    expirationTime = aura_env.savedExpiration[unitName] or 0,\n                    name = unitName,    \n                    index = aura_env.savedExpiration[unitName] or 0,\n                    rdyTimer = aura_env.savedExpiration[unitName] or 0,\n                    successStatus = 0,\n                    aggro = 0,\n                    debuff = 0,\n                }\n            end\n        end\n        return true;\n    end\n    \n    -- Process Taunt Events\n    if (subEvent == \"SPELL_CAST_SUCCESS\") or (subEvent == \"SPELL_MISSED\") then\n        if s[sourceName] then\n            local timestamp = GetTime();\n            local destName = select(7, ...)\n            local spellName = select(11, ...)\n            if (spellName == \"Taunt\") or (spellName == \"Challenging Shout\") then\n                if (subEvent == \"SPELL_CAST_SUCCESS\") then\n                    s[sourceName] = {\n                        show = true,\n                        changed = true,      \n                        progressType = \"timed\",\n                        duration = 10,\n                        expirationTime = timestamp + 10,\n                        name = sourceName,\n                        index = timestamp + 10,\n                        rdyTimer = timestamp + 10,\n                        successStatus = 1,\n                        aggro = s[sourceName].aggro,\n                        debuff = s[sourceName].debuff,\n                    }\n                    aura_env.savedDuration[sourceName] = s[sourceName].duration;\n                    aura_env.savedExpiration[sourceName] = s[sourceName].expirationTime;\n                end\n                if (subEvent == \"SPELL_MISSED\") then\n                    local reason = select(13, ...)\n                    if (reason == \"RESIST\") then\n                        s[sourceName] = {\n                            show = true,\n                            changed = true,      \n                            progressType = \"timed\",\n                            duration = 10,\n                            expirationTime = timestamp + 10,\n                            name = sourceName,\n                            index = timestamp + 10,\n                            rdyTimer = timestamp + 10,\n                            successStatus = 2,\n                            aggro = s[sourceName].aggro,\n                            debuff = s[sourceName].debuff,\n                        }\n                        aura_env.savedDuration[sourceName] = s[sourceName].duration;\n                        aura_env.savedExpiration[sourceName] = s[sourceName].expirationTime;\n                    end\n                end        \n            end\n        end\n    end\n    \n    -- Process Aura Events (Shadow of Ebonroc)\n    if (subEvent == \"SPELL_AURA_APPLIED\") then\n        local dbfm = select(11, ...)\n        if dbfm == \"Shadow of Ebonroc\" then\n            s[sourceName] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = s[sourceName].duration,\n                expirationTime = s[sourceName].expirationTime,\n                name = sourceName,    \n                index = s[sourceName].index,\n                rdyTimer = s[sourceName].rdyTimer,\n                successStatus = s[sourceName].successStatus,    \n                aggro = s[sourceName].aggro,\n                debuff = 1,              \n            }\n        end\n    elseif (subEvent == \"SPELL_AURA_REMOVED\") then\n        local dbfm = select(11, ...)\n        if dbfm == \"Shadow of Ebonroc\" then\n            s[sourceName] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = s[sourceName].duration,\n                expirationTime = s[sourceName].expirationTime,\n                name = sourceName,    \n                index = s[sourceName].index,\n                rdyTimer = s[sourceName].rdyTimer,\n                successStatus = s[sourceName].successStatus,    \n                aggro = s[sourceName].aggro,\n                debuff = 0,              \n            }\n        end\n        \n    end\n    \n    -- Check for successStatus reset\n    if s[sourceName] then\n        if (GetTime() >= s[sourceName].rdyTimer) and (s[sourceName].rdyTimer ~= 0) then\n            s[sourceName] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = s[sourceName].duration,\n                expirationTime = s[sourceName].expirationTime,\n                name = sourceName,    \n                index = s[sourceName].index,\n                rdyTimer = 0,\n                successStatus = 0,\n                aggro = s[sourceName].aggro,\n                debuff = s[sourceName].debuff,\n            }  \n        end\n        updateThreat()\n        return true;\n    end\nend",
