env.maxMarkStack..\" STACKS\"\n            end\n            --set progress bar times\n            aura_env.timer=12\n            aura_env.expirationTime=GetTime()+12\n            return true\n        end\n        --start of combat\n    elseif event==\"ENCOUNTER_START\" then \n        --reset\n        aura_env.markCount=0\n        aura_env.message=\"\"\n        --set progress bar times\n        aura_env.timer=20\n        aura_env.expirationTime=GetTime()+20\n        \n        return true\n        \n    elseif event == \"ENCOUNTER_END\" then\n        \n        --reset\n        aura_env.markCount=0\n        aura_env.message=\"\"       \n        return true\n    end\n    \nend\n\n\n",
