					["custom"] = "--[[\nIndexes:\n- 1: Mob name\n- 2: Kills required\n- 3: Kills recorded so far\n- 4: Wing Number (for sorting in the list)\n- 5: Mob Number (for sorting in the list). The first mob of the wing gets the value 1, second mob gets the value 2 etc.\n]]\naura_env.trash = {\n    ['16017'] = {\"Patchwork Golem\",10,0,aura_env.config['construction'],1},\n    ['16024'] = {\"Embalming Slime\",20,0,aura_env.config['construction'],2},\n    ['16018'] = {\"Bile/Sludge Belcher\",11,0,aura_env.config['construction'],3},\n    ['16021'] = {\"Living Monstrosity\",5,0,aura_env.config['construction'],4},\n    ['16243'] = {\"Plague Slime\",4,0,aura_env.config['plague'],1},\n    ['16168'] = {\"Stoneskin Gargoyle\",8,0,aura_env.config['plague'],2},\n    ['16193'] = {\"Skeletal Smith\",4,0,aura_env.config['military'],1},\n    ['16164'] = {\"Shade of Naxxramas\",2,0,aura_env.config['military'],2},\n    ['16145'] = {\"Deathknight Captain\",12,0,aura_env.config['military'],3},\n    ['16146'] = {\"Deathknight\",14,0,aura_env.config['military'],4},\n    ['16194'] = {\"Unholy Weapon (any type)\",8,0,aura_env.config['military'],5},\n    ['16163'] = {\"Deathknight Cavalier\",9,0,aura_env.config['military'],6},\n    ['16165'] = {\"Necro Knight\",6,0,aura_env.config['military'],7},\n    ['16446'] = {\"Plagued Gargoyle\",4,0,aura_env.config['military'],8},\n    ['15976'] = {\"Venom Stalker\",2,0,aura_env.config['spider'],1},\n    ['15978'] = {\"Crypt Reaver\",1,0,aura_env.config['spider'],2},\n    ['16453'] = {\"Necro Stalker\",4,0,aura_env.config['spider'],3},\n    ['15977'] = {\"Infectious Skitterer\",90,0,aura_env.config['spider'],4},\n    ['15979'] = {\"Tomb Horror\",2,0,aura_env.config['spider'],5},\n    ['16447'] = {\"Plagued Ghoul\",16,0,aura_env.config['spider'],6},\n    ['16368'] = {\"Necropolis Acolyte\",12,0,aura_env.config['spider'],7},\n    ['16449'] = {\"Spirit of Naxxramas\",2,0,aura_env.config['spider'],8},\n}",
