						["custom_type"] = "stateupdate",
						["custom"] = "function(allstates,event,_,subEvent,...)\n    if event == \"PLAYER_ENTERING_WORLD\" then\n        -- set up the list\n        for k,v in pairs(aura_env.trash) do\n            if not allstates[k] then\n                allstates[k] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"string\",\n                    id = k,\n                    name = v[1],\n                    stacks = v[2],\n                    total = v[3],\n                    remaining = v[2] - v[3],\n                    wing = v[4],\n                    mobOrder = v[5]\n                }\n            end\n        end\n        \n        return true\n        \n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" and subEvent == \"UNIT_DIED\" then\n        local unitGUID = select(6,...)\n        local unitID = select(6,strsplit('-',unitGUID))\n        \n        -- 16215 is Unholy Staff and 16216 is Unholy Swords. This is just to make it simpler to log a weapon kill since you just need x amount of kills on any weapon, so the table of mobs required is using the Unholy Axe (16194) ID to track them.\n        if unitID == '16215' or unitID == '16216' then\n            unitID = '16194'\n        elseif unitID == '16029' then -- Sludge Belcher -> Bile Retcher (shared requirements)\n            unitID = '16018'\n        end\n        \n        if aura_env.trash[unitID] then\n            aura_env.trash[unitID][3] = aura_env.trash[unitID][3] + 1\n            \n            for _,state in pairs(allstates) do\n                if state.name == aura_env.trash[unitID][1] then\n                    state.total = state.total + 1\n                    state.changed = true\n                    state.remaining = state.stacks - state.total\n                    \n                    return true\n                end\n            end\n        end\n    end\nend",
