						["custom"] = "function(allstates, event, _, subEvent, _, _, sourceName, _, _, _, _, _, _, _,spellName)\n    if subEvent == \"SPELL_CAST_SUCCESS\" and UnitExists(sourceName) and UnitClass(sourceName) == \"Warrior\"  then \n        if spellName == \"Challenging Shout\"then\n            local duration = 600\n            local expiration = GetTime() + duration\n            aura_env.aoeTable[sourceName] = {\n                duration = duration,\n                expirationTime = expiration,\n            }\n        end\n        if aura_env.isPot(spellName) then\n            local duration = 120\n            local expiration = GetTime() + duration\n            aura_env.potTable[sourceName] = {\n                duration = duration,\n                expirationTime = expiration,\n            }\n        end\n        if aura_env.potTable[sourceName] == nil then \n            aura_env.potTable[sourceName] = {}\n            aura_env.potTable[sourceName].expirationTime = 0\n        end\n        \n        if aura_env.aoeTable[sourceName] == nil then \n            aura_env.aoeTable[sourceName] = {}\n            aura_env.aoeTable[sourceName].expirationTime = 0\n        end\n        if (aura_env.potTable[sourceName].expirationTime < GetTime()) and (aura_env.aoeTable[sourceName].expirationTime < GetTime()) then\n            aura_env.readyTable[sourceName] = true\n        else\n            aura_env.readyTable[sourceName] = false\n        end\n        \n        if (aura_env.readyTable[sourceName] == true) or (aura_env.readyTable[sourceName] == false) then\n            allstates[sourceName] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = 600,\n                expirationTime = math.max(aura_env.potTable[sourceName].expirationTime, aura_env.aoeTable[sourceName].expirationTime),\n                caster = sourceName,\n                autoHide = false,\n            }\n            return true\n        end\n    end\nend\n\n\n",
