				["init"] = {
					["custom"] = "local function guid_to_name(guid) \n    local name, server = select(6, GetPlayerInfoByGUID(guid))\n    if nil == name or nil == server then\n        return nil\n    elseif '' == server then\n        return name\n    else\n        return name .. '-' .. server\n    end\nend\n\nlocal function map_names_to_guids()\n    local guid_by_name = {}\n    local guid = UnitGUID('player')\n    guid_by_name[guid_to_name(guid)] = guid\n    for i = 1, MAX_RAID_MEMBERS do\n        guid = UnitGUID('raid' .. tostring(i))\n        if nil ~= guid then\n            guid_by_name[guid_to_name(guid)] = guid\n        end\n    end\n    for i = 1, MAX_PARTY_MEMBERS do\n        guid = UnitGUID('party' .. tostring(i))\n        if nil ~= guid then\n            guid_by_name[guid_to_name(guid)] = guid\n        end\n    end\n    return guid_by_name\nend\n\nlocal function find_loot_target(guid_by_name, sand_looted_by_name, slot, sand_needed, quantity)\n    local loot_target\n    local most = 0\n    for loot_candidate = 1, MAX_RAID_MEMBERS do\n        local name = GetMasterLootCandidate(slot, loot_candidate)\n        if nil ~= name then\n            local sand_looted = sand_looted_by_name[name] or 0\n            local capacity = sand_needed - sand_looted\n            if capacity == quantity then\n                return loot_candidate\n            elseif capacity > most then\n                most = capacity\n                loot_target = loot_candidate\n            elseif name == UnitName('player') and nil == loot_target then\n                loot_target = loot_candidate\n            end\n        end\n    end\n    return loot_target\nend\n\nlocal function not_master_loot_sand()\n    return 0 ~= (select(1, select(2, GetLootMethod()))) or GetLootThreshold() > 1\nend\n\nlocal function map_names_to_sand_looted(players)\n    local sand_looted_by_name = {}\n    for guid,quantity in pairs(players) do\n        local name = guid_to_name(guid)\n        if nil ~= name then\n            sand_looted_by_name[name] = quantity\n        end\n    end\n    return sand_looted_by_name\nend\n\nlocal function is_sand(slot)\n    local link = GetLootSlotLink(slot)\n    if nil == link then\n        return false\n    end\n    local sand_id = 19183\n    link = string.sub(link, select(2, string.find(link, ':')) + 1, string.len(link))\n    link = string.sub(link, 1, select(1, string.find(link, ':')) - 1)\n    return link == tostring(sand_id)\nend\n\nlocal function on_loot_opened()\n    if not_master_loot_sand() then\n        return\n    end\n    \n    aura_env.awards = {}\n    aura_env.quantity = {}\n    local guid_by_name = map_names_to_guids()\n    local sand_looted_by_name = map_names_to_sand_looted(aura_env.players)\n    \n    for slot,properties in pairs(GetLootInfo()) do\n        if is_sand(slot) then\n            local quantity = properties['quantity']\n            local loot_target = find_loot_target(guid_by_name, sand_looted_by_name, slot, aura_env.config.sand_needed, quantity)\n            if nil ~= loot_target then\n                GiveMasterLoot(slot, loot_target)\n                local name = GetMasterLootCandidate(slot, loot_target)\n                sand_looted_by_name[name] = sand_looted_by_name[name] or 0\n                sand_looted_by_name[name] = sand_looted_by_name[name] + quantity\n                aura_env.awards[slot] = guid_by_name[name]\n                aura_env.quantity[slot] = quantity\n            end\n        end\n    end\nend\n\nlocal function on_loot_slot_cleared(slot)\n    local guid = aura_env.awards[slot]\n    if nil == guid then\n        return\n    end\n    local quantity = aura_env.quantity[slot]\n    if nil == quantity then\n        return\n    end\n    aura_env.players[guid] = aura_env.players[guid] or 0\n    aura_env.players[guid] = aura_env.players[guid] + quantity\n    aura_env.quantity[slot] = nil;\n    aura_env.awards[slot] = nil;\nend\n\n\naura_env.players = {}\naura_env.awards = {}\naura_env.quantity = {}\naura_env.on_event = {}\naura_env.on_event['LOOT_OPENED'] = on_loot_opened\naura_env.on_event['LOOT_SLOT_CLEARED'] = on_loot_slot_cleared\n\n",
